    inserir(tabela_hash,
            ['herbivoria', 'Tipo de predatismo entre um animal herbívoro e os vegetais de que se alimenta.'])
    inserir(tabela_hash,
            ['herpetofauna', 'Conjunto das espécies de répteis e anfíbios que vivem em uma determinada região.'])
